import React, { useState } from "react";
import NavBarAddContact from "../Layouts/NavBarAddContact";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function AddContact(props) {
  const { state } = props.location;
  const { us } = state;
  let navigate = useNavigate()
  const [contact, setContact] = useState({
    name: "",
    address: "",
    phone: "",
    email: "",
    data_of_birth: ""
  });
  const { name, address, phone, email, data_of_birth } = contact;
  const inputchanges = (e) => {
    setContact({ ...contact, [e.traget.name]: e.traget.value });
  };
  const submitForm = async(e) => {
    e.preventDefault();
    await axios.post("http://localhost:8080/contacts/new", contact);
    navigate('/')
  }

  return (
    <div className="container">
      <NavBarAddContact />
      <br />
      <div className="row">
        <div className="col-md-6 offset-md-3 broder rounder p-4 mt-2 shadow">
          <h3 className="text-center m-4">New Contact</h3>
          <br />
          <form onSubmit={(e) => submitForm(e)}>
            <div className="mb-2">
              <label htmlFor="Name" className="form-label">
                Name
              </label>
              <input
                type="text"
                className="form-control"
                placeholder="Enter Name*"
                name="name"
                value={name}
                onChange={(e) => inputchanges(e)}
              />
            </div>
            <div className="mb-2">
              <label htmlFor="Address" className="form-label">
                Address
              </label>
              <input
                type="text"
                className="form-control"
                placeholder="Enter Address*"
                name="Address"
                value={address}
                onChange={(e) => inputchanges(e)}
              />
            </div>
            <div className="mb-2">
              <label htmlFor="" className="form-label">
                Phone Number
              </label>
              <input
                type="tel"
                className="form-control"
                placeholder="Enter Phone Number*"
                name="Phone"
                value={phone}
                onChange={(e) => inputchanges(e)}
              />
            </div>
            <div className="mb-2">
              <label htmlFor="E-mail" className="form-label">
                E-mail
              </label>
              <input
                type="email"
                className="form-control"
                placeholder="Enter E-mail"
                name="E-mail"
                value={email}
                onChange={(e) => inputchanges(e)}
              />
            </div>
            <div className="mb-2">
              <label htmlFor="" className="form-label">
                Date Of Birth
              </label>
              <input
                type="date"
                placeholder="Enter Date Of Birth (optional)"
                className="form-control"
                name="DOB"
                value={data_of_birth}
                onChange={(e) => inputchanges(e)}
              />
            </div>
            <button type="submit" className="btn btn-outline-primary mx-2">
              Submit
            </button>
            <button type="reset" className="btn btn-outline-danger mx-2">
              Clear
            </button>
          </form>
          <p className="text-end text-danger" color="#808080">
            * Mandatory Field
          </p>
        </div>
      </div>
    </div>
  );
}
export default AddContact;
