import React, { useState, useEffect } from "react";
import NavBarHome from "../Layouts/NavBarHome";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const Home = () => {
  const user_id = { user_id: parseInt(localStorage.getItem("userData")) };
  const [contacts, setContacts] = useState([]);
  useEffect(() => {
    loadContacts();
  }, []);
  const { id } = useParams()
  const loadContacts = async () => {
    try {
      const results = await axios.get("http://localhost:8080/contacts/all", {
        params: user_id,
      });
      console.log(results.data);
      setContacts(results.data);
    } catch (error) {
      console.error("There was an error fetching the contacts!", error);
    }
  };
  const deleteContact = async (id) => {
    const results = await axios.delete(`http://localhost:8080/contacts/${id}`);
    console.log(results.data);
    toast("Contacts deleted successfully");
    loadContacts();
  };
  return (
    <div className="container">
      <NavBarHome data={user_id} />
      <br />
      <div className="contacts-list">
        <table className="table text-center border shadow">
          <thead>
            <tr>
              <th scope="id">ID</th>
              <th scope="Name">Name</th>
              <th scope="Address">Address</th>
              <th scope="Phone">Phone</th>
              <th scope="DOB">Email</th>
              <th scope="Action">Action</th>
            </tr>
          </thead>
          <tfoot>Total Records {contacts.length}</tfoot>
          <tbody className="table-group-divider">
            <br />
            {contacts.map((contact, index) => (
              <tr>
                <th scope="row">{index + 1}</th>
                <td>{contact.name}</td>
                <td>{contact.address}</td>
                <td>{contact.phone}</td>
                <td>{contact.email}</td>
                <tb>
                  <Link
                    className="btn btn-primary mx-2"
                    to={`/edit/${contact.id}`}
                  >
                    Edit
                  </Link>
                  <button className="btn btn-outline-primary mx-2">View</button>
                  <button
                    onClick={() => deleteContact(contact.id)}
                    className="btn btn-danger mx-2"
                  >
                    Remove
                  </button>
                </tb>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};
export default Home;
